msg.payload = [
    msg.payload.r.toString(16).padStart(2, '0'),
    msg.payload.g.toString(16).padStart(2, '0'),
    msg.payload.b.toString(16).padStart(2, '0')
].join('');
return msg;

// Access the payload
let value = parseFloat(msg.payload);

let threshold = 30;
if (value > threshold) {
    msg.payload = `Alert: Temperature value (${value}) exceeds the threshold (${threshold}).`;
} else {
    msg.payload = `Temperature value (${value}) is within the safe range.`;
}

return msg;

